package longestPalindrome;

import org.junit.Test;

import static org.junit.Assert.*;

/**
 * Created by julien on 28/03/2016.
 */
public class SolutionTest {

    @Test
    public void longestPalindrome() throws Exception {
        Solution sol = new Solution();

        assertEquals("aa",sol.longestPalindrome("aa"));
        assertEquals("aba",sol.longestPalindrome("aabacd"));
        assertEquals("vgtgv",sol.longestPalindrome("azertyhbcvhhfxcvbgdfvvtfvgtgvgg"));

        assertEquals("",
                sol.longestPalindrome(""));
    }
    @Test
    public void longestPalindrome2() throws Exception {
        Solution2 sol = new Solution2();

        assertEquals("aa",sol.longestPalindrome("aa"));
        assertEquals("aba",sol.longestPalindrome("aabacd"));
        assertEquals("vgtgv",sol.longestPalindrome("azertyhbcvhhfxcvbgdfvvtfvgtgvgg"));

        assertEquals("",
                sol.longestPalindrome(""));
    }
}